package com.fulan.api.security.domain;import java.io.Serializable;import java.util.Date;import com.fasterxml.jackson.databind.annotation.JsonDeserialize;import com.fasterxml.jackson.databind.annotation.JsonSerialize;import com.fulan.application.util.json.LongJsonDeserializer;import com.fulan.application.util.json.LongJsonSerializer;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;/** * 资源实体类 * @author yinyuyan 2018/1/5 * */@Data@ApiModel(value = "Resource", description = "资源实体类")public class Resource implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = 1L;	/*	 * id	 */	@ApiModelProperty(value = "主键")	@JsonSerialize(using = LongJsonSerializer.class)    @JsonDeserialize(using = LongJsonDeserializer.class)	private Long id;	/*	 * 资源名称	 */	@ApiModelProperty(value = "资源名称")	private String resourceName;	/*	 * 是否禁用	 */	@ApiModelProperty(value = "是否禁用")	private Boolean enabled;	/*	 * 图标	 */	@ApiModelProperty(value = "图标")	private String icon;	/*	 * 资源类型 （M：菜单  B：按钮）	 */	@ApiModelProperty(value = "资源类型 （M：菜单  B：按钮）")	private String resourceType;	/*	 * 菜单路径	 */	@ApiModelProperty(value = "菜单路径")	private String url;	/*	 * 父节点id	 */	@ApiModelProperty(value = "父节点id")	private String parentId;	/*	 * 层级	 */	@ApiModelProperty(value = "层级")	private String layer;	/*	 * 顺序	 */	@ApiModelProperty(value = "顺序")	private String seq;	/*	 * 创建者Id	 */	@ApiModelProperty(value = "创建者Id")	private Long createById;	/*	 * 创建者名称	 */	@ApiModelProperty(value = "创建者名称")	private String createByName;	/*	 * 创建时间	 */	@ApiModelProperty(value = "创建时间")	private String createTime;	/*	 * 修改者ID	 */	@ApiModelProperty(value = "修改者ID")	private Long updateById;	/*	 * 修改者名称	 */	@ApiModelProperty(value = "修改者名称")	private String updateByName;	/*	 * 修改时间	 */	@ApiModelProperty(value = "修改时间")	private Date updateTime;	}