package com.fulan.api.security.service;import java.util.List;import java.util.Set;import org.springframework.cloud.netflix.feign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestParam;import com.fulan.api.security.domain.AccountRole;import com.fulan.application.util.domain.Response;import com.fulan.application.util.page.PageInfo;@FeignClient(name = "security")public interface AccountRoleService {	/**	 * 根据账号id查找	 * @param accountId	 * @return	 */	@PostMapping("/account/findByAccountId")	List<AccountRole> findByAccountId(Long accountId);	/**	 * 根据id查找	 * @param id	 * @return	 */	@PostMapping("/account/findById")	AccountRole findById(Long id);	/**	 * 新增账号角色	 * @param accountRole	 * @return	 */	@PostMapping("/accountRole/add")	Response<String> save(AccountRole accountRole);	/**	 * 根据id删除账号角色	 * @param id	 * @return	 */	@PostMapping("/accountRole/delete")	int deleteById(Long id);	/**	 * 分页查询账户角色	 * @param pageNo	 * @param pageSize	 * @param accountRole	 * @return	 */	@PostMapping("/accountRole/listByPage")	PageInfo<AccountRole> listByPage(@RequestParam(name="pageNo")Integer pageNo,@RequestParam(name="pageSize")Integer pageSize,@RequestBody AccountRole accountRole);		/**	 * 根据id查询角色	 * @param accountId	 * @return	 */	@GetMapping("/manageAccount/selectAccountRoleManageVo")	List<String> selectAccountRoleManageVo(@RequestParam(name="accountId") String accountId);	/**	 * 查询用户权限	 * @param accountId	 * @return	 */	@GetMapping("/manageAccount/selectByAccountId")	List<String> selectByAccountId(@RequestParam(name="accountId") String accountId);		}