package com.fulan.api.security.service;import java.util.List;import org.springframework.cloud.netflix.feign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestParam;import com.fulan.api.security.domain.AgentBasicInformation;import com.fulan.api.security.domain.AgentBranchInfomation;import com.fulan.application.util.domain.Response;@FeignClient(name = "security")public interface AgentBranchInformationService {	/**	 * 根据agentId查找机构信息	 * @param account	 * @return	 */	@PostMapping("/agentBranchInformation/getAgentBranchInfoById")	Response<AgentBranchInfomation> getAgentBranchInfoById(@RequestParam("agentId") String agentId);	/**	 * 查询全部的代理人工号和公司名称集合	 * @return	 */	@GetMapping("/agentBranchInformation/selectAllAgentBranchInformation")	List<AgentBranchInfomation> selectAllAgentBranchInformation();}