package com.fulan.application.token;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.PropertySource;import org.springframework.stereotype.Component;import lombok.Data;@Data@Component@EnableConfigurationProperties@ConfigurationProperties(prefix = "elearn.token.jwt")@PropertySource(value = { "classpath:/security-jwt.properties" }, ignoreResourceNotFound = true)public class TokenConfig {		private  String issuer = "elearn";    private  String seed = "ZWxlYXJu";    private  int TTL = 3600000;      private  int refreshSpan = 3300000;      private  int refreshTTL;          }