package com.fulan.application.controller;import java.util.List;import org.slf4j.Logger;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import com.fulan.api.security.domain.AgentBasicInformation;import com.fulan.application.service.AgentBasicInformationService;@RestController@RequestMapping(value = "/agentBasicInformation")public class AgentBasicInformationController {	@Autowired	AgentBasicInformationService agentBasicInformationService;		@RequestMapping(value="/selectAllAgentBasicInformation",method=RequestMethod.GET)	@ResponseBody	public List<AgentBasicInformation> selectAllAgentBasicInformation(){		  try {			  return agentBasicInformationService.selectAllAgentBasicInformation();	        } catch (Exception e) {	        	System.out.println("查询异常----"+e.getMessage());	            return null;	        }			}	}