package com.fulan.application.controller;import java.util.List;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import com.fulan.api.security.domain.Account;import com.fulan.api.security.domain.AgentBasicInformation;import com.fulan.api.security.domain.AgentBranchInfomation;import com.fulan.application.service.AgentBranchInfomationService;import com.fulan.application.util.domain.Response;import com.fulan.core.monitoring.log.annotation.NoLog;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import io.swagger.annotations.ApiParam;@NoLog@Api(tags = "AgentBranchInfo", description = "机构信息接口")@RestController@RequestMapping(value = "/agentBranchInformation")public class AgentBranchInformationController {	@Autowired	AgentBranchInfomationService agentBranchInfomationService;	private static final Logger logger = LoggerFactory.getLogger(AgentBranchInformationController.class);	@ApiOperation(value = "机构基本信息", notes = "机构基本信息", response = Response.class)	@RequestMapping(value = "/getAgentBranchInfoById", produces = { "application/json;charset=utf-8" }, method = RequestMethod.POST)	public Response<AgentBranchInfomation> getAgentBranchInfoById(@ApiParam(name = "agentId", value = "代理人Id") @RequestParam(required=true) String agentId) {		try {			Response<AgentBranchInfomation> resp=new Response<AgentBranchInfomation>(Response.SUCCESS, "查询基本机构信息成功");			return agentBranchInfomationService.getAgentBranchInfoById(Long.valueOf(agentId));		} catch (Exception e) {			logger.error(e.getMessage());			return new Response<AgentBranchInfomation>(Response.ERROR,"根据代理人id查找机构信息失败");		}	}		@RequestMapping(value="/selectAllAgentBranchInformation",method=RequestMethod.GET)	@ResponseBody	public List<AgentBranchInfomation> selectAllAgentBasicInformation(){		 try {			 return agentBranchInfomationService.selectAllAgentBranchInformation();	        } catch (Exception e) {	        	logger.error("查询异常----"+e.getMessage());	            return null;	        }	}}