package com.fulan.application.controller;import javax.servlet.http.HttpServletRequest;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import com.baomidou.mybatisplus.plugins.Page;import com.fulan.api.security.domain.RoleResource;import com.fulan.application.service.RoleResourceService;import com.fulan.application.util.domain.Response;import com.fulan.core.monitoring.log.annotation.NoLog;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;@NoLog@Api(tags = "RoleResourceApi", description = "角色资源接口")@RestController@RequestMapping(value = "/roleResource")public class RoleResourceController {	@Autowired	RoleResourceService roleResourceService;	private static final Logger logger = LoggerFactory.getLogger(RoleResourceController.class);	@ApiOperation(value = "新增角色资源", notes = "新增角色资源", response = Response.class)	@RequestMapping(value = "/add", produces = { "application/json;charset=utf-8" }, method = RequestMethod.POST)	public Response<String> addRole(HttpServletRequest request, @RequestBody RoleResource roleResource) {				return roleResourceService.save(roleResource);	}	@ApiOperation(value = "删除角色资源", notes = "删除角色资源", response = Response.class)	@RequestMapping(value = "/delete", produces = { "application/json;charset=utf-8" }, method = RequestMethod.POST)	public Response<String> delRoleResource(HttpServletRequest request, @RequestParam(required=true) Long id) {		roleResourceService.deleteById(id);		return new Response<String>(Response.SUCCESS, "删除角色资源成功");	}		@ApiOperation(value = "查找角色资源列表", notes = "查找角色资源列表", response = Response.class)	@RequestMapping(value = "/listByPage", produces = { "application/json;charset=utf-8" }, method = RequestMethod.POST)	public Page<RoleResource> listByPage(HttpServletRequest request,@RequestBody RoleResource roleResource,			@RequestParam(required=true) Integer pageNo,@RequestParam(required=true) Integer pageSize){		return roleResourceService.listByPage(pageNo, pageSize, roleResource);	}}