package com.fulan.application.mapper;import java.util.List;import org.apache.ibatis.annotations.Many;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Repository;import org.springframework.web.bind.annotation.RequestParam;import com.baomidou.mybatisplus.mapper.BaseMapper;import com.fulan.api.security.domain.Role;import com.fulan.api.security.vo.RoleAndResourceVo;@Repositorypublic interface RoleMapper extends BaseMapper<Role>{		@Select("select * from role")	@Results({		@Result(property = "id" ,column="id"),		@Result(property = "roleName" ,column="role_name"),		@Result(property = "enabled" ,column="enabled"),		@Result(property = "parentId" ,column="parent_id"),        @Result(property = "resource" , javaType=List.class, column = "id",                many = @Many(select = ("com.fulan.application.mapper.ResourceMapper.listResource")))	})	List<RoleAndResourceVo> listRAR();		@Select("select * from role where id = #{id}")	@Results({		@Result(property = "id" ,column="id"),		@Result(property = "roleName" ,column="role_name"),		@Result(property = "enabled" ,column="enabled"),		@Result(property = "parentId" ,column="parent_id"),        @Result(property = "resource" , javaType=List.class, column = "id",                many = @Many(select = ("com.fulan.application.mapper.ResourceMapper.listResource")))	})	List<RoleAndResourceVo> listRARById(String id);		@Select("select * from role where enabled = '1'")	@Results({		@Result(property = "id" ,column="id"),		@Result(property = "roleName" ,column="role_name"),		@Result(property = "enabled" ,column="enabled"),		@Result(property = "parentId" ,column="parent_id")	})	List<Role> seleList();    }