package com.fulan.application.service;import java.util.List;import org.springframework.web.bind.annotation.RequestParam;import com.baomidou.mybatisplus.plugins.Page;import com.fulan.api.security.domain.Resource;import com.fulan.api.security.domain.Role;import com.fulan.api.security.vo.RoleAndResourceVo;import com.fulan.application.util.domain.Response;public interface RoleService {	/**	 * 根据id查找	 * @param id	 * @return	 */	Role findById(Long id);	/**	 * 新增	 * @param role	 * @return	 */	Response<String> save(Role role);	/**	 * 更新	 * @param role	 * @return	 */	Response<String> updateRole(Role role);	/**	 * 根据id删除	 * @param id	 * @return	 */	void deleteById(Long id);	/**	 * 分页查询	 * @param role	 * @param page	 * @return	 */	Page<Role> list(Role role,Page<Role> page);		/**	 * 角色增加资源	 * @param resourceId	 * @param roleId	 * @return	 */	Response<String> addResource(Long resourceId,Long roleId);	/**	 * 删除角色资源	 * @param roleId	 * @param roleId	 * @return	 */	void deleteResource(Long resourceId,Long roleId);	/**	 * 查找角色资源列表	 * @param roleId	 * @return	 */	List<Resource> findResources(Long roleId,String resourceType);		/**	 * 根据id查询所有角色并其关联的所有资源权限	 * * @return	 */	List<RoleAndResourceVo> findRoleAndResource(String id);	/**	 * manage查询所有角色	 * * @return	 */	List<Role> listForManage();		/**	 * manage新增	 * @param role	 * @return	 */	Response<String> saveForManage(Role role);		/**	 * manage新增	 * @param role	 * @return	 */	Response<String> updateForManage(Role role,String type);	/**	 * 根据roleId查找资源	 * @param roleId	 * @return	 */	List<Resource> findResourceByRoleId(@RequestParam("roleId")Long roleId);		List<Role> seleList();	}